admresource.script:
        
        /*if(!document.frmAdmRes.target[0].checked && !document.frmAdmRes.target[1].checked)
		document.frmAdmRes.target[1].checked=true;*/
        dojo.require("dijit.form.Button");
        dojo.require("dijit.form.ValidationTextBox");

        function jsValida(frm) {
            if(!isEmpty(dojo.byId('url').value) && isEmpty(dojo.byId('img').value) && !dojo.byId('wbNoFile_img')) {
                alert('Especifique una imagen para el banner');
                dojo.byId('img').focus();
                return false;
            }

            if(isEmpty('img').value && isEmpty(dojo.byId('code').value)) {
                alert('Defina una configuración básica');
                dojo.byId('url').focus();
                return false;
            }
            return true;
        }
