admresource.script:
          

    function jsClear(frm)
    {
            frm.btntexto.value=''; 
            frm.lnktexto.value='';
    }
    
    function isNumber(pIn)
    {
       pCaracter=pIn.value;
       for (var i=0;i<pCaracter.length;i++)
       {
           var sByte=pCaracter.substring(i,i+1);
           if (sByte<"0" || sByte>"9")
           {
               pIn.focus();
               alert('Este campo debe ser numérico.');
               return false;
           }
       }
       return true;
    }
    
    function jsValida(frm)
    {
        if(!isNumber(frm.level)) {
            return false;
        }else {
            var lvl = parseInt(frm.level.value, 10);
            if(lvl<1) {
                alert("El nivel de despliegue debe ser mayor que cero.");
                return false;
            }
        }
        return true;
    }
