admresource.script:

    function jsValida(frm) {
        var level = parseInt(frm.level.value, 10);
        if(isNaN(level)) {
            alert('The field Display Level must be integer');
            return false;
        }
        if(level < 1) {
            alert("The field Display Level must be non zero");
            return false;
        }
        return true;
    }
