admresource.script:

dojo.require("dijit.form.Button");
dojo.require("dijit.form.ValidationTextBox");

dojo.addOnLoad(
    function() {
        dojo.connect(dijit.byId("btnSave"), "onClick", function(){ if(jsValida(dojo.byId("frmAddNew"))){ dojo.byId("frmAddNew").submit(); } } );
        dojo.connect(dijit.byId("btnReset"), "onClick", function(){ dojo.byId("frmAddNew").reset(); } );
    }
);

function jsValida(frm) {
    var cluster = dojo.byId('cluster').value;
    if( !cluster ) {
        alert('Especifique el Subtipo de Banner');
        dojo.byId('cluster').focus();
        return false;
    }
    return true;
}
